import React, { Component } from 'react';
import axios from 'axios';
import TableRow from './TableRow.jsx';
import { BrowserRouter, Switch, Route, Link} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
const config = require('../config.json');



export default class Index extends Component {

  constructor(props) {
      super(props);

      this.state = {
        order: [],
        user_data:JSON.parse(localStorage.getItem('user_data'))
      };




      authenticateUser();


    }
    componentDidMount(){
      axios.get(config.ServerAddress+'/order')
        .then(response => {
          this.setState({ order: response.data });
        })
        .catch(function (error) {
          console.log(error);
        })
    }
    tabRow(){
      return this.state.order.map(function(object, i){
          return <TableRow obj={object} key={i} />;
      });
    }

    authenticateUser(){
      const access_token = user_data.orderapp_user_token
      console.log(access_token);
      axios.post(config.ServerAddress+'/user/authenticate', obj)



        .then(res=> {if(res.data.message==="invalid"){
          this.setState({
            invalid_login:true
          });


        }else{

          this.setState({invalid_login:false,user_data:res.data},()=>localStorage.setItem('user_data',JSON.stringify(this.state.user_data)));

        }

    });

    }


    render() {


      if(this.state.user_data===null || this.state.user_data===undefined){
        this.props.history.push('/login');

      }

      return (
        <div>
        <h3 align="center">Business List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Order Name</th>
              <th>Customer Name</th>
              <th>Address</th>

              <th colSpan="2">Action</th>
            </tr>
          </thead>
          <tbody>
            { this.tabRow() }
          </tbody>
        </table>
      </div>


      );
    }
  }
