import React, { Component} from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import axios from 'axios';




class App extends Component {
  constructor(props) {
    super(props);


    this.state = {
        username: '',
        register:false,
        password1: '',
        password2:'',

        submitted: false
    };

    this.handleRegister = this.handleRegister.bind(this);
    this.handleUserName = this.handleUserName.bind(this);
    this.handlePassword1 = this.handlePassword1.bind(this);

    this.handlePassword2 = this.handlePassword2.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

handleUserName(e) {

  this.setState({ username: e.target.value });
}

handlePassword1(e) {

  this.setState({ password1: e.target.value });
}
handlePassword2(e) {

  this.setState({ password2: e.target.value });
}
handleRegister(){

  this.setState({register:true});
}

handleSubmit(e) {
  e.preventDefault();

  this.setState({ submitted: true });

  const obj={ username:this.state.username,
              password:this.state.password1 }

  if (this.state.username && this.state.password1) {
    axios.post('http://localhost:4000/user/authenticate', obj)
        .then(res => console.log(res.data));

  }
}



  render() {
    var passwordmatch='';
    var registersystem='';
    if(this.state.password1!=this.state.password2){
      passwordmatch=<div className="help-block Warning">Two password are not the same</div>
    }
    if(this.state.register==true){
      registersystem=<div> <input type="text" id="password" className="fadeIn third" name="login" placeholder="Retype Password" value={this.state.password2} onChange={this.handlePassword2}/>
      <input type="submit" className="fadeIn fourth" value="Register"/>
      </div>
    }





    return (
      <div className="wrapper fadeInDown ">

      <div id="formContent">
      <h1 className="">Sysco POS App </h1>
      <div className="fadeIn first">

    </div>
    <form  onSubmit={this.handleSubmit}>

      <input type="text" id="login" className="fadeIn second" name="login"  value={this.state.username} onChange={this.handleUserName} placeholder="Username"/>
      {this.state.submitted && !this.state.username &&
                            <span className="help-block">Username is required</span>
                        }
      <input type="text" id="password" className="fadeIn third" name="login" placeholder="Password" value={this.state.password1} onChange={this.handlePassword1}/>
      {this.state.submitted && !this.state.password &&
                            <div className="help-block">Password is required</div>
                        }

      {passwordmatch}
      {this.state.register?registersystem:<input type="submit" className="fadeIn fourth" value="Log In"/>}


    </form>

    <div id="formFooter">
    {/* <Link  className="nav-link" >Forgot Password ?</Link> */}

      <Link to={''} onClick={this.handleRegister} className="nav-link" >Need an account ? </Link>
    </div>

      </div>
      </div>











    );
  }
}
export default App;
