    }

    const navbar=
                  <div className="collapse navbar-collapse" id="navbarSupportedContent">

                    <ul className="navbar-nav mr-auto">
                    <li className="nav-item">
                        <Link to={'/index'} className="nav-link">Home</Link>
                      </li>
                      <li className="nav-item">
                        <Link to={'/create'} className="nav-link">Create</Link>
                      </li>

                    </ul>
                  </div>;


    return (


        <div>
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to={'/index'} className="navbar-brand"><h1>Sysco POS App </h1></Link>
            {this.state.isAuth?navbar:''}

          </nav> <br/>

          <Switch>
              <Route path='/login' component={Login} user_data={this.user_data} />
              <Route exact path='/create' component={ Create } />
              <Route path='/edit/:id' component={ Edit } />
              <Route path='/' component={ Index } user_data={this.user_data} />
          </Switch>



        </div>

        </div>


    );
  }
}
export default App;
