import React, { Component } from 'react';
import axios from 'axios';


class DocumentInput extends React.Component {
  render() {
    return <tr><td>
    <input type="text" 
    className="form-control"
    name={ `name-${ this.props.index }` } 
    value={this.state.item_name}
    onChange={this.onChangeItemName}
    />
    </td>
    <td>
    <input type="number" 
    className="form-control"
    name={ `amount-${ this.props.index }` }
    value={this.state.amount}
    onChange={this.onChangeAmount}
    />
    </td>
    <td>
    <input type="number" 
    className="form-control"
    name={ `unitvalue-${ this.props.index }` }
    value={this.state.unit_value}
    onChange={this.onChangeUnitValue}
    />
    </td>
    <td>
    <input type="text" 
    className="form-control"
    name={ `subtot-${ this.props.index }` }
     readOnly 
    value={this.state.subtot}
    onChange={this.onChangeSubTot}/>
    </td>
    </tr>

  }
}


export default class Create extends Component {
  constructor(props) {
    super(props);
    this.onChangeOrderName = this.onChangeOrderName.bind(this);
    this.onChangeItemName = this.onChangeItemName.bind(this);
    this.onChangeAmount = this.onChangeAmount.bind(this);
    this.onChangeUnitValue = this.onChangeUnitValue.bind(this);
    this.addItem = this.addItem.bind(this);
    this.onChangeGstNumber = this.onChangeGstNumber.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      person_name: '',
      business_name: '',
      business_gst_number:'',
      amount:0.0,
      unit_value:0.0,
      subtot:0.0,
      itemset:[]
    }
  }
  onChangeOrderName(e) {
    this.setState({
      order_name: e.target.value
    });
  }
  onChangeItemName(e) {
    this.setState({
      item_name: e.target.value
    })  
  }
  onChangeAmount(e) {


    this.setState({
      amount: e.target.value,


    },()=>this.setState({
      subtot:this.state.unit_value * this.state.amount

    }))



  }
  onChangeUnitValue(e) {
    this.setState({
      unit_value: e.target.value,
      subtot:this.state.unit_value * this.state.amount
    },()=>this.setState({
      subtot:this.state.unit_value * this.state.amount

    }))  
  }
  onChangeSubTot(e) {
    this.setState({
     subtot: e.target.value
    })
  }
  onChangeCustomerName(e) {
    this.setState({
     customer_name: e.target.value
    })
  }
  addItem(){
    const itemset = this.state.itemset.concat(DocumentInput);
    this.setState({ itemset });     



  }
  onChangeGstNumber(e) {
    this.setState({
      business_gst_number: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    const obj = {
      person_name: this.state.person_name,
      business_name: this.state.business_name,
      business_gst_number: this.state.business_gst_number
    };
    axios.post('http://localhost:4000/business/add', obj)
        .then(res => console.log(res.data));

    this.setState({
      person_name: '',
      business_name: '',
      business_gst_number: ''
    })
  }

  render() {
    const itemset = this.state.itemset.map((Element, index) => {
      return <tr><td>
      <input type="text" 
      className="form-control"
      name={ `name-${ this.props.index }` } 
      value={this.state.item_name}
      onChange={this.onChangeItemName}
      />
      </td>
      <td>
      <input type="number" 
      className="form-control"
      name={ `amount-${ this.props.index }` }
      value={this.state.amount}
      onChange={this.onChangeAmount}
      />
      </td>
      <td>
      <input type="number" 
      className="form-control"
      name={ `unitvalue-${ this.props.index }` }
      value={this.state.unit_value}
      onChange={this.onChangeUnitValue}
      />
      </td>
      <td>
      <input type="text" 
      className="form-control"
      name={ `subtot-${ this.props.index }` }
       readOnly 
      value={this.state.subtot}
      onChange={this.onChangeSubTot}/>
      </td>
      </tr>
    });
    return (

        <div style={{ marginTop: 10 }}>
            <h3>Add New Order</h3>
            <form onSubmit={this.onSubmit}>
                <div className="form-group">
                    <label>Order Name:  </label>
                    <input 
                      type="text" 
                      className="form-control" 
                      value={this.state.order_name}
                      onChange={this.onChangeOrderName}
                      />
                </div>
                <div className="form-group">
                    <label>Items: </label>
                    <table>
                      <tbody>
                      <tr>
                        <td className="col-l-2">Name</td>
                        <td className="col-l-2">Amount</td>
                        <td className="col-l-2">Unit Price $</td>
                        <td className="col-l-2">Sub Total</td>
                        <td>
                        <button onClick={this.addItem} className="glyphicon glyphicon-plus">
                        <span className="input-group-addon" id="sizing-addon1">+</span>
                          </button>
                        </td>
                      </tr>


                      {itemset}



                      </tbody>
                      </table>
                    <input type="text" 
                      className="form-control"
                      value={this.state.customer_name}
                      onChange={this.onChangeCustomerName}
                      />
                </div>
                <div className="form-group">
                    <label>GST Number: </label>
                    <input type="text" 
                      className="form-control"
                      value={this.state.business_gst_number}
                      onChange={this.onChangeGstNumber}
                      />
                </div>
                <div className="form-group">
                    <input type="submit" value="Register Business" className="btn btn-primary"/>
                </div>
            </form>
        </div>
    )
  }
}
