import React, { Component} from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import axios from 'axios';




class App extends Component {
  constructor(props) {
    super(props);

    // reset login status
    this.props.dispatch(userActions.logout());

    this.state = {
        username: '',
        password: '',
        submitted: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

handleChange(e) {
  const { name, value } = e.target;
  this.setState({ [name]: value });
}

handleSubmit(e) {
  e.preventDefault();

  this.setState({ submitted: true });
  const { username, password } = this.state;
  const { dispatch } = this.props;
  if (username && password) {
      dispatch(userActions.login(username, password));
  }
}



  render() {
    return (
      <div className="wrapper fadeInDown ">
      <h1 className="">Sysco POS App </h1>
      <div id="formContent">
      <div className="fadeIn first">

    </div>
    <form  onSubmit={this.handleSubmit}>
      <input type="text" id="login" className="fadeIn second" name="login"  value={username} onChange={this.handleChange} placeholder="Username"/>
      {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
      <input type="text" id="password" className="fadeIn third" name="login" placeholder="Password" value={password} onChange={this.handleChange}/>
      {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
      <input type="submit" className="fadeIn fourth" value="Log In"/>
    </form>

    <div id="formFooter">
    <Link to={'/'} className="nav-link">Forgot Password ?</Link>
      <Link to={'/'} className="nav-link">Need an account ? </Link>
    </div>

      </div>
      </div>











    );
  }
}
export default App;
