import React, { Component} from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import axios from 'axios';




class App extends Component {
  constructor(props) {
    super(props);


    this.state = {
        username: '',
        register:false,
        password: '',
        submitted: false
    };

    this.handleRegister = this.handleRegister.bind(this);
    this.handleUserName = this.handleUserName.bind(this);
    this.handlePassword = this.handlePassword.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

handleUserName(e) {

  this.setState({ username: e.target.value });
}

handlePassword(e) {

  this.setState({ password: e.target.value });
}
handleRegister(){
  this.setState({register:true});
}

handleSubmit(e) {
  e.preventDefault();

  this.setState({ submitted: true });

  const obj={ username:this.state.username,
              password:this.state.password }

  if (this.state.username && this.state.password) {
    axios.post('http://localhost:4000/user/authenticate', obj)
        .then(res => console.log(res.data));

  }
}



  render() {
    return (
      <div className="wrapper fadeInDown ">
      <h1 className="">Sysco POS App </h1>
      <div id="formContent">
      <div className="fadeIn first">

    </div>
    <form  onSubmit={this.handleSubmit}>

      <input type="text" id="login" className="fadeIn second" name="login"  value={this.state.username} onChange={this.handleUserName} placeholder="Username"/>
      {this.state.submitted && !this.state.username &&
                            <div className="help-block">Username is required</div>
                        }
      <input type="text" id="password" className="fadeIn third" name="login" placeholder="Password" value={this.state.password} onChange={this.handlePassword}/>
      {this.state.submitted && !this.state.password &&
                            <div className="help-block">Password is required</div>
                        }
      {this.state.register==true &&
      <input type="text" id="password" className="fadeIn third" name="login" placeholder="Password" value={this.state.password} onChange={this.handlePassword}/>

      }
      <input type="submit" className="fadeIn fourth" value="Log In"/>
    </form>

    <div id="formFooter">
    {/* <Link  className="nav-link" >Forgot Password ?</Link> */}
      <lable className="nav-link" onclick={this.handleRegister}>Need an account ? </lable>
    </div>

      </div>
      </div>











    );
  }
}
export default App;
