import React, { Component } from 'react';
import axios from 'axios';
import TableRow from './TableRow.jsx';
import OrderDetailView from './orderDetailView.component.jsx';
import { BrowserRouter, Switch, Route, Link} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import update from 'immutability-helper';
const config = require('../config.json');



export default class Index extends Component {

  constructor(props) {
      super(props);

      this.orderId="";

      this.view = this.view.bind(this);


      this.state = {


        order: [],
        orderDetailView:{},

        user_data:JSON.parse(localStorage.getItem('user_data'))
      };





    }

    componentWillMount(){





      if(this.state.user_data===null){



        this.props.history.push('/login');

      }else{
        const access_token = this.state.user_data.orderapp_user_token;

      const getOrders = {
        method: 'get',
        headers: { Authorization: "Bearer " + access_token },


        url:config.ServerAddress+'/order',
      };

      axios(getOrders).then(response => {
        if(response.data.message==="sessionExpired"){
          this.props.handleLogout();

          this.props.history.push('/login');

        }else{
          //this.props.handleUserName();

          this.setState({ order: response.data.orders });


          // this.setState({jasper});



          this.state.order.map((order_obj)=>
          {
            let element = JSON.stringify(order_obj._id);

            let temporderView = Object.assign({}, this.state.orderDetailView);

            temporderView[element] = ""; 




            //element1 = element+"";
            //console.log(element)

            // var tempobj={};
            // tempobj[element]="";

            this.setState({
              orderDetailView:temporderView

            });



          });














        }







        }).catch(function (error) {
          console.log(error);
        })

      }


    }


    view(current_order){
      //console.log(this.state.orderDetailView)





      document.getElementById(current_order._id+"hidebtn").hidden=false;
      document.getElementById(current_order._id+"viewbtn").hidden=true;

      const text = <div><OrderDetailView order={current_order} /></div>;
      let data = this.state.orderDetailView;
      let element_id = JSON.stringify(order_obj._id);
      data[element_id] = text;
      this.setState({
        orderDetailView:data
      });
    //   console.log(data)
    //   let index = data.findIndex(function(c) {
    //     //console.log(id)
    //     return c.id === current_order._id; 
    //   });
    //   console.log(index)
    //   var updatedorder = update(data[index], {text: {$set: text}});

    //   var newData = update(data, {
    //     $splice: [[index, 1, updatedorder]]
    // });


    //   this.setState({
    //     orderDetailView:newData
    //   });




      // document.getElementById(current_order._id+"detailView").innerHTML = <div><OrderDetailView order={current_order} /></div>;
      // this.setState({
      //   orderDetailView:<div><OrderDetailView order={current_order} /></div>
      // });
      //let objectid = current_order._id


      // let markers = [ ...this.state.showOrderDetail ];
      // markers[objectid] = {...markers[objectid],objectid: value};

      //this.setState({ showOrderDetail:markers });
      //console.log(this.showOrderDetail)









    }
    hide(current_order){

      document.getElementById(current_order._id+"hidebtn").hidden=true;
      document.getElementById(current_order._id+"viewbtn").hidden=false;
      document.getElementById(current_order._id+"detailView").innerHTML="";
      this.setState({
        orderDetailView:""
      })


    }




    render() {





      return (
        <div>
        <h3 align="center">Order List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Order Name</th>
              <th>Customer Name</th>
              <th>Address</th>

              <th colSpan="3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.state.order.map((single_order)=><tr key={single_order._id}>

            <td>{single_order.order_name}</td>


            <td>{single_order.customer_name}   </td>

            <td>{single_order.address}

            </td>
            <td>
                {/* <Link className="btn btn-primary" to={"/orderDetailView"}>View</Link> */}

                <button onClick={this.view.bind(this,single_order)} id={single_order._id+"viewbtn"} className="btn btn-primary">View</button>
                <button onClick={this.hide.bind(this,single_order)} hidden={true} id={single_order._id+"hidebtn"}  className="btn btn-primary">Hide</button>

                </td>


                <td>


                  <button onClick={this.delete} className="btn btn-danger">Delete</button>

                </td>




          <td id={single_order._id+"detailView"}></td>







          {/* {this.showOrderDetail.concat(single_order._id,"")}    */}



            {/* {single_order.order_name} */}

            {/* {this.showOrderDetail=this.showOrderDetail.concat(single_order._id="")} */}

            {/* <td>{this.orderId=single_order._id}</td>  */}













            </tr>)}




          </tbody>
        </table>


      </div>


      );
    }
  }
