import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

class TableRow extends Component {

  constructor(props) {
        super(props);
        this.delete = this.delete.bind(this);
        this.view = this.view.bind('');

        this.state = {detail_order_view : '',

      };
    }
    delete() {
        axios.get('http://localhost:4000/business/delete/'+this.props.obj._id)
            .then(console.log('Deleted'))
            .catch(err => console.log(err))
    }
    view(obj_id){
      this.setState({
        detail_order_view:obj_id
      })


    }
  render() {

      let object_id=this.props.obj._id


    return (
        <tr>
          <td>
            {this.props.obj.order_name}
          </td>
          <td>
            {this.props.obj.customer_name}
          </td>
          <td>
            {this.props.obj.address}
          </td>
          <td>
          <button onClick={this.view(object_id)} className="btn btn-primary">View</button>
          </td>
          <td>
            <Link to={"/edit/"+object_id} className="btn btn-primary">Edit</Link>
          </td>
          <td>
            <button onClick={this.delete} className="btn btn-danger">Delete</button>
          </td>


        </tr>






    );
  }
}

export default TableRow;
