import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const config = require('../config.json');

class TableRow extends Component {

  constructor(props) {
        super(props);

        this.delete = this.delete.bind(this);
        this.view = this.view.bind(this);
        this.hide = this.hide.bind(this);

        this.state = {
          alert_type:"",
          alert_message:"",
          alerting : false
        }


    }


    alertAndTimeout(type,message){

      this.setState({
        alerting : true,
        alert_type:type,
        alert_message:message
      },()=>setTimeout(
        function() {
            this.setState({
              alerting: false,
              alert_type:"",
              alert_message:""



            });
        }
        .bind(this),
        5000
    ));

    }


    delete() {
      const access_token = this.props.access_token;
      const dataObj = {id : this.props.order_key}
      const deleteOrder = {
        method: 'post',
        headers: { Authorization: "Bearer " + access_token },
        data: dataObj,


        url:config.ServerAddress+'/order/delete',
      };

      axios(deleteOrder).then(response => {
        if(response.data.message==="sessionExpired"){
          this.props.handleLogout();

          this.props.history.push('/login');

        }else{



          if(response.data.message==="remove_successful"){
            console.log("okay")



            this.alertAndTimeout("success","Order Removed Succefully !");



          }else if(response.data.message==="remove_fail"){
            this.alertAndTimeout("danger","Order Removing Failed !")

          }


        }


        }).catch(function (error) {
          console.log(error);
        })



    }
    view(){
      document.getElementById(this.props.order_key+"itemlist").hidden=false;
      document.getElementById(this.props.order_key+"hideButton").hidden=false;
      document.getElementById(this.props.order_key+"viewButton").hidden=true;




    }

    hide(){
      document.getElementById(this.props.order_key+"itemlist").hidden=true;
      document.getElementById(this.props.order_key+"hideButton").hidden=true;
      document.getElementById(this.props.order_key+"viewButton").hidden=false;


    }


  render() {

    var alert_element = 
      <div className="row ">

        <div className="col-sm">

          <div className={"alert alert-"+this.state.alert_type} role="alert">
            {this.state.alert_message}
          </div>

        </div>


      </div>

    var tableRow=

                <tr>

                <td>
                  {this.props.obj.order_name}
                </td>
                <td>
                  {this.props.obj.customer_name}
                </td>
                <td>
                  {this.props.obj.address}
                </td>
                <td>
                <button onClick={this.view} id={this.props.order_key+"viewButton"} className="btn btn-primary">View</button>
                <button hidden={true} onClick={this.hide} id={this.props.order_key+"hideButton"} className="btn btn-primary">Hide</button>
                </td>
                <td>
                  <Link to={"/edit/"+this.props.obj._id} className="btn btn-primary">Edit</Link>
                </td>
                <td>
                  <button onClick={this.delete} className="btn btn-danger">Delete</button>
                </td>



              <td hidden={true} id={this.props.order_key+"itemlist"}>
                <table >
                  <tbody>
                  {this.props.obj.items.map((item)=><tr key={item.item_number} >
                <td>{item.item_name}</td>
                <td>{item.quantity}</td>
                <td>{item.unit_value}</td>
                <td>{item.subtot}</td>
              </tr>)}

                  </tbody>
                </table>



              </td>
              </tr>

    return (

      (this.state.alerting && this.state.alert_type)?alert_element:tableRow





    );
  }
}

export default TableRow;
