import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Switch, Route, Link} from 'react-router-dom';
import './App.css';
import Create from './components/create.component.jsx';
import Edit from './components/edit.component';
import Index from './components/index.component.jsx';
import Login from './components/login.component.jsx';

class App extends Component {
  constructor(props){
    super(props)
    this.isAuth=false;


    //this.props.user_data=sessionStorage.getItem('user_data')

    this.state={
      user_data:JSON.parse(localStorage.getItem('user_data')),
      user_name:""
    };

    //console.log();


  }
  componentWillMount(){
    if(this.state.user_data!==undefined){
      this.isAuth=true;

      this.setState({
        user_name:this.state.user_data.username

      });




    }else{
      this.isAuth=false;
    }

  }
  setUserData(){


  }



  render() {





    const navbar=
                  <div className="collapse navbar-collapse" id="navbarSupportedContent">

                    <ul className="navbar-nav mr-auto">
                      <li className="nav-item">
                        <Link to={'/index'} className="nav-link">Home</Link>
                      </li>
                      <li className="nav-item">
                        <Link to={'/create'} className="nav-link">Create Order</Link>
                      </li>
                      <li className="nav-item" onClick>
                        <Link to={'/login'} className="nav-link">Logout</Link>
                      </li>



                    </ul>

                    <ul class="navbar-nav ml-auto">
                      <li className="nav-item "><h1>Hi,{''}   {this.state.user_name} </h1></li>
                    </ul>

                  </div>;


    return (



        <div>
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to={'/index'} className="navbar-brand"><h1>Sysco POS App </h1></Link>
            {this.isAuth?navbar:''}

          </nav> <br/>

          <Switch>
              <Route path='/login' component={Login} />
              <Route exact path='/create' component={ Create } />
              <Route path='/edit/:id' component={ Edit } />
              <Route path='/' component={ Index } />
          </Switch>



        </div>

        </div>


    );
  }
}
export default App;
