import React, { Component} from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import axios from 'axios';




class App extends Component {
  constructor(props) {
    super(props);


    this.state = {
        username: '',
        registerState:'',
        password1: '',
        password2:'',

        submitted: false
    };

    this.handleRegister = this.handleRegister.bind(this);
    this.handleUserName = this.handleUserName.bind(this);
    this.handlePassword1 = this.handlePassword1.bind(this);

    this.handlePassword2 = this.handlePassword2.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

handleUserName(e) {

  this.setState({ username: e.target.value });
}

handlePassword1(e) {

  this.setState({ password1: e.target.value });
}
handlePassword2(e) {

  this.setState({ password2: e.target.value });
}
handleRegister(registerValue){

  this.setState({register:registerValue});
}

handleSubmit(e) {
  e.preventDefault();

  this.setState({ submitted: true });

  const obj={ username:this.state.username,
              password:this.state.password1 }

  if (this.state.username && this.state.password1) {
    axios.post('http://localhost:4000/user/authenticate', obj)
        .then(res => console.log(res.data));

  }
}



  render() {

    var registersystem='';
    if((this.state.password1==this.state.password2) && this.state.password1!=''){
      this.setState({registerState:''})
      this.state.registerState='trueregister';
    }

    switch(this.state.registerState){
      case 'notregistering': return(
          <div>
            <input type="submit" className="fadeIn fourth" value="Log In"/>
            <div id="formFooter">
              <Link to={''} onClick={()=>this.handleRegister('registering')} className="nav-link" >Need an account ? </Link>
            </div>
          </div>);
      case 'registering' : return(
          <div>
            <input type="text" id="password" className="fadeIn third" name="login" placeholder="Retype Password" value={this.state.password2} onChange={this.handlePassword2}/>
            <div id="formFooter">
              <Link to={''} onClick={()=>this.handleRegister('notregistering')} className="nav-link" >Log In </Link>
            </div>
          </div>);
      case 'trueregister' : return(
          <div>
            <input type="text" id="password" className="fadeIn third" name="login" placeholder="Retype Password" value={this.state.password2} onChange={this.handlePassword2}/>
            <div id="formFooter">
              <Link to={''} onClick={()=>this.handleRegister('notregistering')} className="nav-link" >Log In </Link>
            </div>
          </div>);
    }
    var passwordNotMatched=<div className="help-block Warning">Two password are not the same</div>;

    var passwordMatchedRegister = <input type="submit" className="fadeIn fourth" value="Register"/>;








    return (
      <div className="wrapper fadeInDown ">

      <div id="formContent">
      <h1 className="">Sysco POS App </h1>
      <div className="fadeIn first">

    </div>
    <form  onSubmit={this.handleSubmit}>

      <input type="text" id="login" className="fadeIn second" name="login"  value={this.state.username} onChange={this.handleUserName} placeholder="Username"/>
      {this.state.submitted && !this.state.username &&
                            <span className="help-block">Username is required</span>
                        }
      <input type="text" id="password" className="fadeIn third" name="login" placeholder="Password" value={this.state.password1} onChange={this.handlePassword1}/>
      {this.state.submitted && !this.state.password &&
                            <div className="help-block">Password is required</div>
                        }

      {this.state.register?registersystem:logginstate}




    </form>



      </div>
      </div>











    );
  }
}
export default App;
