import React, { Component } from 'react';
import axios from 'axios';
import TableRow from './TableRow.jsx';
import OrderDetailView from './orderDetailView.component.jsx';
import { BrowserRouter, Switch, Route, Link} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
const config = require('../config.json');



export default class Index extends Component {

  constructor(props) {
      super(props);
      this.showOrderDetail=[]

      this.view = this.view.bind(this);


      this.state = {

        order: [],

        user_data:JSON.parse(localStorage.getItem('user_data'))
      };





    }

    componentDidMount(){





      if(this.state.user_data===null){



        this.props.history.push('/login');

      }else{
        const access_token = this.state.user_data.orderapp_user_token;

      const getOrders = {
        method: 'get',
        headers: { Authorization: "Bearer " + access_token },


        url:config.ServerAddress+'/order',
      };

      axios(getOrders).then(response => {
        if(response.data.message==="sessionExpired"){
          this.props.handleLogout();

          this.props.history.push('/login');

        }else{
          //this.props.handleUserName();

          this.setState({ order: response.data.orders });


        }


        }).catch(function (error) {
          console.log(error);
        })

      }


    }


    view(order){

      //access_token={this.state.user_data.orderapp_user_token}

      document.getElementById(order._id+"hidebtn").hidden=false;
      document.getElementById(order._id+"viewbtn").hidden=true;

        this.props.showOrderDetail[order._id] =<div><OrderDetailView order={order} /></div>




      // return this.state.order.map(function(object, i){
      //   //console.log(this.state.user_data.orderapp_user_token)
      //     return <TableRow obj={object} key={i} order_key={object._id} access_token={access_token}  />;
      // });
    }
    hide(order){

      document.getElementById(order._id+"hidebtn").hidden=true;
      document.getElementById(order._id+"viewbtn").hidden=false;


    }




    render() {




      return (
        <div>
        <h3 align="center">Order List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Order Name</th>
              <th>Customer Name</th>
              <th>Address</th>

              <th colSpan="3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.state.order.map((order)=><tr key={order._id}>

            {this.showOrderDetail=this.showOrderDetail.concat(order._id="")}


            <td>{order.order_name}</td>
            <td>{order.customer_name}</td>
            <td>{order.address}</td>
            <td>
                {/* <Link className="btn btn-primary" to={"/orderDetailView"}>View</Link> */}

                <button onClick={this.view.bind(this,order)} id={order._id+"viewbtn"} className="btn btn-primary">View</button>
                <button onClick={this.hide.bind(this,order)} hidden={true} id={order._id+"hidebtn"}  className="btn btn-primary">Hide</button>

                </td>
                <td>


                  <button onClick={this.delete} className="btn btn-danger">Delete</button>

                </td>
                <td>
                  {this.showOrderDetail[order._id]}
                </td>


            </tr>)}




          </tbody>
        </table>


      </div>


      );
    }
  }
