import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Switch, Route, Link} from 'react-router-dom';
import './App.css';
import Create from './components/create.component.jsx';
import Edit from './components/edit.component';
import Index from './components/index.component.jsx';
import Login from './components/login.component.jsx';

class App extends Component {
  constructor(props){
    super(props)

    this.user_data={}
    let sessdata=localStorage.getItem('user_data');
    this.user_data=sessdata;
    console.log(sessdata);
    //this.props.user_data=sessionStorage.getItem('user_data')
    console.log(Object.isExtensible(this.user_data));
    isAuth=true;





  }



  render() {

    if(this.user_data==undefined){
      this.isAuth=false;

    }



    const navbar=
                  <div className="collapse navbar-collapse" id="navbarSupportedContent">

                    <ul className="navbar-nav mr-auto">
                    <li className="nav-item">
                        <Link to={'/index'} className="nav-link">Home</Link>
                      </li>
                      <li className="nav-item">
                        <Link to={'/create'} className="nav-link">Create</Link>
                      </li>

                    </ul>
                  </div>;


    return (


        <div>
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to={'/index'} className="navbar-brand"><h1>Sysco POS App </h1></Link>
            {this.isAuth?navbar:''}

          </nav> <br/>

          <Switch>
              <Route path='/login' component={Login} user_data={this.user_data} isAuth={this.isAuth} />
              <Route exact path='/create' component={ Create } />
              <Route path='/edit/:id' component={ Edit } />
              <Route path='/' component={ Index } user_data={this.user_data} />
          </Switch>



        </div>

        </div>


    );
  }
}
export default App;
